(()=>{document.querySelectorAll(".photo img").forEach(e=>{e.addEventListener("click",c=>{let o=c.target.getAttribute("data-popup");window.innerWidth<=768?window.location.href=o:(document.getElementById("popup-iframe").src=o,document.querySelector(".popup").classList.add("show"))})});document.querySelector(".close").addEventListener("click",()=>{t()});document.addEventListener("keyup",e=>{e.key==="Escape"&&t()});function t(){document.querySelector(".popup").classList.remove("show")}window.addEventListener("message",function(e){e.data==="iframeClick"&&t()});})();
